mapper.select:
select rb.`no` as 'no', rb.title as title, rb.content as content, us.nickname as nickname, us.profile as profile, rb.complete as complete, rb.`view` as 'view', rb.create_at as create_at, rb.update_at as update_at, ( select count(*) from `like` as li where li.board_no = rb.`no` AND li.type = 2 ) as 'like', us.nickname as user_nickname, us.no as user_no from request_board as rb left join chickengak.user as us on rb.USER_NO = us.no
rb.title like CONCAT('%',#{title},'%') rb.content like CONCAT('%',#{content},'%') us.nickname = #{nickname}
order by rb.no desc

mapper.update:
update request_board
title = #{title}, content = #{content}, complete = #{complete}, update_at = now()
where no = #{no}
